import axios from 'axios'

const LOGIN_API_BASE_URL = 'http://localhost:8080/api/v1/login'

function axiosRequestToken() {
    // create a promise for the axios request
    const promise = axios.post(LOGIN_API_BASE_URL, null, {
        params: {
            username: pUsername,
            password: pPassword
        }
    });

// using .then, create a new promise which extracts the data
const dataPromise = promise.then((response) => response.data)

// return it
return dataPromise
}

// now we can use that data from the outside!
axiosRequestToken()
    .then(data => {
        response.json({ message: 'Request received!', data })
    })
    .catch(err => console.log(err))

class TokenService {
    getTokens(pUsername, pPassword) {
        return axios.post(LOGIN_API_BASE_URL, null, {
            params: {
                username: pUsername,
                password: pPassword
            }
        }).then(response => {
            response.data
            // const token_object = response.headers.getAccept;
            // return token_object;

        });
    }


}

export default new TokenService()
