// Importar la biblioteca Axios para realizar solicitudes HTTP
import axios from 'axios'

// URL del servicio de autenticación
const LOGIN_API_BASE_URL = 'http://localhost:8080/api/v1/login'

// Clase que representa un servicio de token
class TokenService{
    // Obtener un token de autenticación
    getTokens(pUsername, pPassword){
        // Realizar una solicitud POST a la URL especificada, pasando el nombre de usuario y la contraseña como parámetros
        return axios.post(LOGIN_API_BASE_URL, null, {
            params: {
                username: pUsername,
                password: pPassword
            }
        // Cuando se recibe una respuesta, asignar el token recibido a la propiedad "token_generated" y devolverlo
        }).then(response => {

                this.token_generated = response.data;

                console.log(this.token_generated)
                return this.token_generated;
        });
    }
}

// Exportar una instancia del servicio de token
export default new TokenService()
